mapper.insert:

    select board_seq.nextval as boardId from dual

insert
    into board
        (
        board_id
        , board_title
        , mem_id
        , board_content
        ,board_reg_date
        , board_url
        ,hit
        ,thumbnail
        )
    values
        (
        ${boardId}
        , #{boardTitle[0]}
        , #{memId[0]}
        , #{boardContent[0]}
        ,sysdate
        , #{boardUrl[0]}
        ,1
        ,#{thumbnail[0]}
        )

mapper.update:
update board
set
board_title=#{boardTitle[0]}
,board_content=#{boardContent[0]}
,board_up_date=sysdate
where board_id=#{boardId[0]}

mapper.select:
select * from ( SELECT ROWNUM AS RNUM, A.* FROM (select board_id, board_title, board_content, board_reg_date, board_up_date, board_url, mem_id, hit, thumbnail from board where 1=1
and mem_id = #{memId}
and (board_title like '%${searchKeywordText}%' or board_content like '%${searchKeywordText}%' or mem_id like '%${searchKeywordText}%')
order by board_reg_date desc) A
where ROWNUM <= #{endPage} ) where RNUM >= #{startPage}
